Scripts.OnMouseUp:
if ( this.isMoving ) then
	this:StopMovingOrSizing();
	this.isMoving = false;
end

Scripts.OnMouseDown:
if ( ( ( not this.isLocked ) or ( this.isLocked == 0 ) ) and ( arg1 == "LeftButton" ) ) then
	this:StartMoving();
	this.isMoving = true;
end
if (arg1 == "RightButton") then
	Dcr_ShowHideButtons();
end

Scripts.OnHide:
if ( this.isMoving ) then
	this:StopMovingOrSizing();
	this.isMoving = false;
end

Scripts.OnClick:
Drc_Options.CheckBox.Active_Debug_Messages = (this:GetChecked() == 1);
if (Drc_Options.CheckBox.Active_Debug_Messages) then
	DcrOptionsFrameAnchor:Enable();
else
	DcrOptionsFrameAnchor:Disable();
end

Scripts.OnShow:
if (Drc_Options.CheckBox.Active_Debug_Messages) then
	this:SetChecked(1);
else
	this:SetChecked(0);
end

if (Drc_Options.CheckBox.Check_For_Abolish) then
	this:SetChecked(1);
else
	this:SetChecked(0);
end

if (Drc_Options.CheckBox.Do_Not_Blacklist_Prio_List) then
	this:SetChecked(1);
else
	this:SetChecked(0);
end

if (Drc_Options.CheckBox.Always_Use_Best_Spell) then
	this:SetChecked(1);
else
	this:SetChecked(0);
end

if (Drc_Options.CheckBox.Random_Order) then
	this:SetChecked(1);
else
	this:SetChecked(0);
end

if (Drc_Options.CheckBox.Scan_Pets) then
	this:SetChecked(1);
else
	this:SetChecked(0);
end

if (Drc_Options.Ingore_Stealthed) then
	this:SetChecked(1);
else
	this:SetChecked(0);
end

Scripts.OnLoad:
Dcr_ThisSetText(DCR_LOC_AF_TYPE [DCR_MAGIC]);
this.CureFunction = Dcr_Cure_Magic;
this.CureType = DCR_MAGIC;
this.CurePos = false;
Dcr_CureTypeCheckBoxes[DCR_MAGIC] = this;

Scripts.OnShow:
if (Drc_Options.CureMagic) then
	this:SetChecked(1);
else
	this:SetChecked(0);
end
if (DCR_CAN_CURE_MAGIC or DCR_CAN_CURE_ENEMY_MAGIC) then
	this:Enable();
else
	this:SetChecked(0);
	this:Disable();
end
Dcr_On_CureOrderCheckBox_Update(this);

